	@Override
	public JSONObject findByGroupsRequisition(Integer gid) {
		JSONObject waitinglist = new JSONObject();

		System.out.println("準備查詢");
		List<GroupMembers> allowlist = gMembersRep.finyByGroupsId(gid, true);

		JSONArray allowArray = new JSONArray();
		for (GroupMembers member : allowlist) {
			JSONObject memberJson = new JSONObject();
			memberJson.put("memberId", member.getMember_id());
			memberJson.put("userId", member.getUser().getUserId());
			memberJson.put("isCheck", member.isCheck());
			String imageBase64 = Base64.getEncoder().encodeToString(member.getMemberImage());
			memberJson.put("image", imageBase64);
			memberJson.put("name", member.getMemberName());
			memberJson.put("groupId", member.getGroups().getGroupId());
			Optional<UUID> adminUserIdOptional = gMembersRep.findByAdmin(member.getGroups().getGroupId());
			if (adminUserIdOptional.isPresent()) {
				UUID adminUserId = adminUserIdOptional.get();
				notificationService.createNotification(adminUserId, member.getUser().getUserId(), "New join Request",
						"You have a new join request from " + member.getUser().getUserAccount(),
						member.getGroups().getGroupId().toString());
			} else {
				System.out.println("找不到管理員");
			}
			allowArray.put(memberJson);
		}

		waitinglist.put("allowList", allowArray);

		return waitinglist;
	}

