	@Override
	public List<Friend> addAllFriends(UUID sendId) {
		List<Friend> friends = friendService.getFollowing(sendId);
		for (Friend myFriend : friends) {
			JSONObject memberJson = new JSONObject();
			User receiver = myFriend.getReceiver();
			User sender = myFriend.getSender();
			UserInfo senderInfo = sender.getUserinfo();
			UserInfo receiverInfo = receiver.getUserinfo();
			memberJson.put("id", myFriend.getId().getReceiveId().toString());

			// 生成群組名稱
			String groupName;
			if (receiverInfo.getUserFirstName() == null || senderInfo.getUserFirstName() == null) {
				groupName = ("My"
						+ (receiverInfo.getUserLastName() != null ? receiverInfo.getUserLastName() : "Friend"))
						+ ("+" + "My"
								+ (senderInfo.getUserLastName() != null ? senderInfo.getUserLastName() : "Friend"));
			} else {
				groupName = (receiverInfo.getUserFirstName()
						+ (receiverInfo.getUserLastName() != null ? receiverInfo.getUserLastName() : "Friend"))
						+ ("+" + senderInfo.getUserFirstName()
								+ (senderInfo.getUserLastName() != null ? senderInfo.getUserLastName() : "Friend"));
			}
			System.out.println(groupName);
			memberJson.put("groupName", groupName);

			UUID receiverId = UUID.fromString(memberJson.getString("id"));
			List<Integer> groupIds = gMembersRep.findGroupIdByStatusAndTwoUserIds(sendId, receiverId);

			if (!groupIds.isEmpty()) {
				// 群組已經存在
				System.out.println("已經有朋友關係了!");
			} else {
				// 創建新的群組和成員
				byte[] originalData = new byte[0];
				addGroupName(sendId, groupName, GroupStatus.FRIENDS, originalData);

				String gidStr = groupsRep.findMyfriendGroupId(sendId, groupName);
				if (gidStr != null) {
					int gid = Integer.parseInt(gidStr);
					boolean exists = existsByGroupMemberId(receiverId, gid);
					if (exists) {
						System.out.println("已經有朋友關係了!");
					} else {
						User user = userRepo.findById(receiverId).orElse(null);
						Groups group = groupsRep.findById(gid).orElse(null);
						if (user != null && group != null) {
							GroupMembers groupMembers = new GroupMembers();
							groupMembers.setUser(user);
							groupMembers.setGroups(group);
							groupMembers.set_admin(false);
							groupMembers.setJoin_at(new Date());
							String memberPhoto = user.getUserinfo().getPhoto();
							if (memberPhoto != null && !memberPhoto.isEmpty()) {
								// 移除前缀
								String base64Image = memberPhoto.split(",")[1]; // 去除 'data:image/jpeg;base64,' 这部分
								byte[] originalDataFd = Base64.getDecoder().decode(base64Image);
								groupMembers.setMemberImage(originalDataFd);
							} else {
								String defaultImage1 = "";
								String defaultImage2 = "";
								String defaultImage3 = "";
								String defaultImage4 = "";
								String defaultImage = defaultImage1 + defaultImage2 + defaultImage3 + defaultImage4;
								byte[] originalDataFd = Base64.getDecoder().decode(defaultImage);
								groupMembers.setMemberImage(originalDataFd);
							}

							gMembersRep.save(groupMembers);
							System.out.println("新增好友群組加入成員....");
						} else {
							System.out.println("無法找到用戶或群組");
						}
					}
				} else {
					System.out.println("無法找到群組ID");
				}
			}
		}
		return friends;
	}

